vector<ll>fact(N,0);
void calc_fact(){
    fact[0] = 1;
    for(int i = 1; i < N; i++)
        fact[i] = (fact[i - 1]*i)%M;
}
ll C(ll n, ll k){
    return (fact[n]*(pow1((fact[k]*fact[n-k])%M,M-2)))%M;
}

